import { React,  Component } from 'react';
  
//step-1
import { connect } from 'react-redux';

//step-4
import EmpAction from './../../actions/empAction.js' //externalData-restapi


import bindActionCreators from 'redux'

class ReactReduxConnection extends Component {
   
   
   
    constructor(props)
    {
     super(props)
     this.state = {}
    }
  
    componentDidMount()
    {
        this.props.empAction();
    }  
  


    render() {
      return (<>
        <div className="container py-2">
            <h2 className="text-center">ReactReduxConnection</h2>
        </div>
        </>)
    }
  }

 //step-3
 //get the data from the Redux-store [state data] by using Reducers[state object]
  mapStateToProps = (state) =>
  {
   state.empReducer
  }

  mapDispatchToProps = (dispatch) =>
  //calling Redux actions...
  {
   return bindActionCreators({ EmpAction }, dispatch)
  }



  
 // export default ReactReduxConnection --> normal react component export


 //step-2

export default connect( mapStateToProps,mapDispatchToProps)(ReactComponent)
