import React, { Component } from 'react'

import { connect } from "react-redux";

import EmpAction from '../../actions/EmpAction';

//global_variable for RESTAPI-url


export default class ReactBootstrap extends Component 
{
    
           
    constructor(props)
    {
        super(props)

        this.state = {

        

        }
    }

    componentDidMount() {
        this.props.EmpAction();
    }
    
    render() 
    {



        return (
            <div className="container text-center">

               <h2>ReduxReactConnection</h2>
             {
                this.props.empdata.map((res,i)=>{
                    return (<ul key={i}>
                        <li>{res.id}</li>
                        <li>{res.name}</li>
                        <li>{res.email}</li>
                    </ul>) 
                })
               
            }



            </div>
        )
    }
    
}
const mapStateToProps = (state) => {
    console.log(state)
    return ({
        empdata : state.EmpReducer
    })
}
const mapDispatchToProps=(dispatch)=>{
    return bindActionCreators({EmpAction}, dispatch)
}
connect (mapStateToProps, mapDispatchToProps)(ReactReduxConnection)
