import React, { Component } from "react";
import axios from 'axios';
import { Link } from 'react-router-dom';

var idno

export default class Edit extends Component {

    constructor(props) {
        super(props)
        this.state = 
        {
           
        }
    }


    render() {

        idno = this.props.match.params.idno

        //this.props->history, location, match
        console.log(idno)

        var { id, name, email, phone } = this.state

        return (<>

            <div className="container p-5">
                <h2>EDIT COMPONENT PAGE</h2>

                <form>

                    <div className="mb-3">
                        <label className="form-label">ID</label>
                        <input type="number" className="form-control" value={id} />

                    </div>

                    <div className="mb-3">
                        <label className="form-label">Name</label>
                        <input type="text" className="form-control" value={name} />

                    </div>


                    <div className="mb-3">
                        <label className="form-label">Email</label>
                        <input type="text" className="form-control" value={email} />

                    </div>


                    <div className="mb-3">
                        <label className="form-label">Mobile No</label>
                        <input type="number" className="form-control" value={phone} />

                    </div>
                    <div>
                        <Link className="btn btn-success mx-2" to="/restapi">GOTO BACK</Link>
                        <button type="submit" className="btn btn-warning text-white" value="update">Update</button>
                    </div>


                </form>


            </div>

        </>)

    }
    
    componentDidMount()
    {
        const url=`http://localhost:3003/user/${idno}`
        axios.get(url).then((result) => this.setState(result.data));
    } 
    


}
