import { React, useState, useEffect } from 'react';
import axios from 'axios'
import Pager from './Pager'


const apiurl = `https://jsonplaceholder.typicode.com/posts`

export default(props) => {

     // State Object Implementation
     var [posts,setStatePosts] = useState([]);

     //select maxposts per action

     var [DefaultmaxDisplayposts] = useState(8);

     //firstButton number

     var [DefaultButton,SetStateDefaultButton] = useState(8);

  
     //Lastindex

     var lastindex=DefaultButton * DefaultmaxDisplayposts //1*8-->
     var FirstIndex=lastindex - DefaultmaxDisplayposts //8-8=0

     var DisplayDefaultButtonPosts=Posts.slic
     //APi calls by hooks
    useEffect(() =>
    {

      axios.get(apiurl).then( (result)=> setStatePosts(result.data))

    },[])

    //total-posts from api

    var totalPosts = posts.length
  
    return(<>
             
     <div className="container p-5">
     <h1 className="text-center py-3">React Hooks Pagination</h1>
     
     
     <br /><br />
      <Pager  totalpostsInAPI={totalPosts} 
      maxDisplayrecordsPerButton={DefaultmaxDisplayposts}/>
     <br /><br />
           {

                posts.map((res,i) => 
                {
                 
                  return(<ul key={i}>
                    
                     <li>

                       <h4>{res.id}.{res.title}</h4>
                        <p>{res.body}</p>
                     </li>


                  </ul>)

               })

           }

        


     </div>


    </>)
 
}
