import React, { Component } from 'react'

import axios from 'axios';




//global_variable for RESTAPI-url
const url = `https://jsonplaceholder.typicode.com/users` //array 

 class Restapi extends Component
{


    constructor(props) {
        super(props)

        this.state = 
        {
           userData: []  //empty array
        }
    }

    render(){
        return(<>
            <div className="container p-5">

                <h3 class="text-center">FETCH RESTAPI DATA USING GLOBALURL/CLIENT URL</h3>


            <table className="table table-dark table-bordered">

            <thead>
                <tr>
                    
                </tr>
            </thead>


            </table>



              {
                this.state.userData.map( (res,i)=>{
                    return(<uL key={i}>

                        <li>{res.id}</li>
                        <li>{res.name}</li>
                        <li>{res.username}</li>

                    </uL>)
                } )
              }


            </div>
            </>)
    }
//restapt-class
componentDidMount()
{
    axios.get(url).then( (result)=> this.setState({ userData:result.data }) );
}

}
export default Restapi
