import React, { useState, useEffect } from 'react';
import axios from 'axios'

const url = `http://localhost:3003/user`

export default () => {
   
  
  var [userData, setStateUserData] = useState({
       uname: '',
       email: '',
       phone: '',
       txtConformation: ''
    })
   
      //Destructuring....
      var {uname,email,phone,txtConformation} = userData;
   /*
   //name-input-text-box
     unameHandling = (e) =>
      {
        this.setState({ uname: e.target.value })
      }
   
   //Email-input-text-box
     emailHandling = (e) =>
      {
        this.setState({ email: e.target.value })
      }
   
    //Phone-input-text-box
     phoneHandling = (e) =>
      {
        this.setState({ phone: e.target.value })
      }
     */

   let fromDataHanding=(e)=>{
         
   }


   submitHandler = (e) => 
   {

    e.preventDefault();

      var FormData=
      {
         'name':this.state.uname,
         'email':this.state.email,
         'phone':this.state.phone
      }
      
      alert(JSON.stringify(FormData));
 

      axios.post(url,FormData)
      .then(() => {
       // window.alert("....CREATED....");
        this.props.history.push('/restapi') 
    });



   }
   
   
        return (<>
            <div className="container p-3">

              <h4 className='py-3 text-center'>Registration Form</h4>
             
             <div className="container w-50">

             <form onSubmit={this.submitHandler.bind(this)}>

                <div className="mb-3">
                  <label className="form-label">Name</label>
                   <input type="text" className="form-control" 
                   value={uname} 
                   onChange={this.unameHandling.bind(this)}/>
                
                </div>

               
                <div className="mb-3">
                  <label className="form-label">Email</label>
                   <input type="email" className="form-control"
                   value={email} 
                   onChange={this.emailHandling.bind(this)}/>
                </div>



                <div className="mb-3">
                  <label className="form-label">Phone</label>
                   <input type="number" className="form-control"
                    value={phone} 
                    onChange={this.phoneHandling.bind(this)}/>
                </div>

                <div className="mb-3">
                  
                   <button type="submit" className="btn btn-primary">CREATE ACCONT</button>
                </div>
                   
                <p className="text-primary">{txtConformation}</p>

             </form>

             </div>

            </div>
            </>)
    }
