import React, { Component } from 'react'

import axios from 'axios';




//global_variable for RESTAPI-url
const url = `https://jsonplaceholder.typicode.com/users` //array 

 class Restapi extends Component
{


    constructor(props) {
        super(props)

        this.state = 
        {
           userData: []  //empty array
        }
    }

    render(){
        return(<>
            <div className="container p-5">

                <h3 class="text-center">FETCH RESTAPI DATA USING GLOBALURL/CLIENT URL</h3>


            <table className="table table-dark table-bordered my-5">

            <thead className="table table-light">
                <tr>
                    <th>ID</th>
                    <th>NAME</th>
                    <th>EMAIL</th>
                    <th>WEDSITE</th>
                    <th>ACTION</th>
                </tr>
            </thead>

              


              <tbody>
              {
                this.state.userData.map( (res,i)=>{
                    return(<tr key={i}>

                        <td>{res.id}</td>
                        <td>{res.name}</td>
                        <td>{res.email}</td>
                        <td>{res.website}</td>
                        <td>
                            <button className>

                            </button>
                        </td>

                    </tr>)
                } )
              }
              </tbody>

           </table> 
           
            </div>
            </>)
    }
//restapt-class
componentDidMount()
{
    axios.get(url).then( (result)=> this.setState({ userData:result.data }) );
}

}
export default Restapi
