import {React, Component } from 'react';
  
//step-1
import { connect } from 'react-redux';

//step-3
import EmpAction from '../../actions/EmpAction.js' //externalData-restapi

 //step-4
import { bindActionCreators } from 'redux'

class ReactReduxConnection extends Component {
   
   
   
    constructor(props)
    {
     super(props)
     this.state = {}
    }
  
    componentDidMount()
    {
        this.props.EmpAction();
    }  
  


    render() {
      return (<>
        <div className="container py-2">
            <h2 className="text-center">ReactReduxConnection</h2>
        
        {
            this.props.empdata.map((res,i) =>
            {
                return(<ul key={i}>
                    <li>{res.id}</li>
                    <li>{res.name}</li>
                    <li>{res.email}</li>
                    <li>{res.phone}</li>
                </ul>)
            })
        }
        
        
        
        </div>
        
        
        </>)
    }
  }

 //step-6
 //get the data from the Redux-store [state data] by using Reducers[state object]
  const mapStateToProps = (state) => {

    console.log(state)

   return({ 
    empdata:state.empReducer
      })
  }
 //step-5
 //calling Redux actions...

 const mapDispatchToProps = (dispatch) => {
   return bindActionCreators({ EmpAction },dispatch)
  }



  
 // export default ReactReduxConnection --> normal react component export


 //step-2

export default connect(mapStateToProps,mapDispatchToProps)(ReactReduxConnection)
