//Contant.js-module

import { React,  Component } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators} from 'redux'

import EmpAction from "../../actions/EmpAction";

//Create Contact Component
class Contact extends Component {
   
   
   
  constructor(props)
  {
   super(props)
   this.state = {}
  }

  componentDidMount()
  {
      this.props.EmpAction();
  }  



  render() {
    return (<>
      <div className="container py-2">
          <h2 className="text-center">ReactReduxConnection</h2>
      
      {
          this.props.empdata.map((res,i) =>
          {
              return(<ul key={i}>
                  <li>{res.id}</li>
                  <li>{res.name}</li>
                  <li>{res.email}</li>
                  <li>{res.phone}</li>
              </ul>)
          })
      }
      
      
      
      </div>
      
      
      </>)
  }
}

//step-6
//get the data from the Redux-store [state data] by using Reducers[state object]
const mapStateToProps = (state) => {

  console.log(state)

 return({ 
  empdata:state.empReducer
    })
}
function mapDispatchToProps(dispatch) 
{
  return bindActionCreators({EmpAction},dispatch)
}
export default connect(mapStateToProps,mapDispatchToProps)(Contact);
