import React, { Component } from "react";
import { connect } from "react-redux";
import EmpAction from '../../actions/EmpAction'
import { bindActionCreators } from "redux"

class ReactReduxConnection extends Component {
    constructor(props) {
        super(props)

        this.state = {}
    }
    componentDidMount() {
        this.props.EmpActio();
    }
    render() {
        return (<>
            <h2>ReduxReactConnection</h2>
           {/** {
                this.props.empdata.map((res,i)=>{
                    return (<ul key={i}>
                        <li>{res.id}</li>
                        <li>{res.name}</li>
                        <li>{res.email}</li>
                    </ul>) 
                })
               
            } */}
            </>)
    }

}
const mapStateToProps = (state) => {
    console.log(state)
    return ({
        empdata:state.EmpReducer
    })
}
const mapDispatchToProps=(dispatch)=>{
    return bindActionCreators({EmpAction}, dispatch)
}
export default connect(mapStateToProps, mapDispatchToProps )(ReactReduxConnection)
